mapper.select:
SELECT SUM(amounttype) amounttype,SUM(amountnumber) amountnumber
FROM library_report_bookcollection_pubyear
WHERE `year`!='不详'
AND `year` <= #{endyear}
AND `year` >= #{startyear}

SELECT year,MONTH,total
FROM library_report_ic_total
GROUP BY year,month;

SELECT year,print_type,print_location,times
FROM library_report_add_times
GROUP BY year,print_type,print_location;

SELECT  year,identity,sum
FROM  library_report_identity_sum
ORDER BY  year ;
